mapper.select:
select re.restarea_idx, re.name, re.tel,
( select img from( select RESTAREA_IDX,AMENITY_TYPE,listagg(icon,',') within group(order by ICON) img from restarea_amenity group by RESTAREA_IDX, AMENITY_TYPE) where restarea_idx = re.restarea_idx and AMENITY_TYPE='A') retAmImgs,
( select img from( select RESTAREA_IDX,AMENITY_TYPE,listagg(icon,',') within group(order by ICON) img from restarea_amenity group by RESTAREA_IDX, AMENITY_TYPE) where restarea_idx = re.restarea_idx and AMENITY_TYPE='B') gasAmImgs,
case when CARWASH_CHK='Y' then 'O' when CARWASH_CHK='N' then 'X' end CARWASH_CHK, case when REPAIR_CHK='Y' then 'O' when REPAIR_CHK='N' then 'X' end REPAIR_CHK, case when CARGOLOUNGE_CHK='Y' then 'O' when CARGOLOUNGE_CHK='N' then 'X' end CARGOLOUNGE_CHK from restarea re
1=1
and re.line_idx = #{lineFlag}
and re.name like '%'||#{keyword}||'%'
and re.carwash_chk =#{carwash_chk}
and re.repair_chk =#{repair_chk}
and re.cargolounge_chk =#{cargolounge_chk}
order by restarea_idx offset (#{pageFlag}-1)*10 rows fetch next 10 rows only

select count(*) cnt
from  restarea re

1=1

and re.line_idx = #{lineFlag}
	

and re.name like '%'||#{keyword}||'%'


and re.carwash_chk =#{carwash_chk}


and re.repair_chk =#{repair_chk}


and re.cargolounge_chk =#{cargolounge_chk}

select restarea_idx
from restarea
where img=#{img} and name=#{name}

mapper.update:
update restarea
img = #{img},
carwash_chk = #{carwash_chk}, repair_chk = #{repair_chk}, cargolounge_chk = #{cargolounge_chk}, name = #{name}, lat = #{lat}, lng = #{lng}, tel = #{tel}, line_idx = #{line_idx}, do_idx = #{do_idx}
where restarea_idx = #{restarea_idx}

mapper.select:
select img
from restarea
where restarea_idx = #{idx}
